mapper.update:
update material_put
material_no = #{materialNo}, material_name = #{materialName}, material_amount = #{materialAmount}, packing_unit = #{packingUnit}, operator = #{operator}, repository_no = #{repositoryNo}, put_time = #{putTime}, state = #{state}, memo = #{memo},
where id = #{id}

mapper.delete:
delete from material_put 
where id in

	#{id}

mapper.update:
update material_put
set state = ${state}
where id in

	#{id}
